#!/usr/bin/env python3# -*- coding: utf-8 -*-#@file   :SeleniumWay.py#@time   :2018/5/26 1:21#@Author :jmgen#@Version:1.0#@Desc   :from selenium import webdriverfrom selenium.webdriver.common.keys import Keysimport timedriver = webdriver.Chrome()driver.get("https://www.lagou.com/")tab=driver.find_element_by_xpath("//*[@id='changeCityBox']/p[1]/a")tab.click()assert u"拉勾网" in driver.titlesearch_input = driver.find_element_by_id('search_input')search_button = driver.find_element_by_id("search_button")search_input.clear()search_input.send_keys("测试工程师")search_button.click()time.sleep(3)assert u"测试工程师" in driver.page_sourcesalary=driver.find_element_by_xpath("//*[@id='order']/li/div[2]/div")salary.click()salarydetail=driver.find_element_by_xpath("//*[@id='order']/li/div[2]/div/ul/li[6]/a")salarydetail.click()area=driver.find_element_by_xpath("//*[@id='filterCollapse']/div[1]/div[2]/div/div[1]/a[2]")area.click()areadetail=driver.find_element_by_xpath("//*[@id='filterCollapse']/div[1]/div[2]/li[2]/div/a[2]")areadetail.click()driver.close()