#!/usr/bin/env python3# -*- coding: utf-8 -*-#@file   :ParserText.py#@time   :2018/5/13 13:15#@Author :jmgen#@Version:1.0#@Desc   :line_dict_uniq = dict()with open("text/视频链接.txt","r") as fb:    for line in fb:        if ":" in line:            #key = line.split("\":")[0]            key = line.split("\":")[1]            if key not in line_dict_uniq.keys():                line_dict_uniq[key] = line                file = open('text/video.txt', 'a+')                str = line.split("\":")[0].split("\"")[1] + " : " + line.split("\":")[1].split("\"")[1]                if str not in file.read():                    file.write(str + "\n\n")                file.close()            else:                continue            # print(line)            print(len(line_dict_uniq))