#!/usr/bin/env python3# -*- coding: utf-8 -*-#@file   :Redemo.py#@time   :2018/5/25 23:12#@Author :jmgen#@Version:1.0#@Desc   :import reimport requestsdef download(url):    if url is None:        return None    user_agent = 'Mozilla/4.0 (compatible; MSIE 5.5; Windows NT)'    headers = {'User-Agent': user_agent}    r = requests.get(url, headers=headers)    if r.status_code == 200:        r.encoding = 'utf-8'        return r.text    return Nonepattern = re.compile(r'(http://movie.mtime.com/(\d+)/)')url="http://theater.mtime.com/China_Guangdong_Province_Shenzen/"response=download(url)urls = pattern.findall(response)#print(urls)pattern = re.compile(r'(http://movie.mtime.com/(\d+)/)')s = pattern.findall("(http://movie.mtime.com/4234234/")print(s)